DEVINST GetDrivesDevInstByDiskNumber(long DiskNumber, UINT DriveType) {
&nbsp;
&nbsp;GUID* guid;
&nbsp;
&nbsp;switch (DriveType) {
&nbsp;case DRIVE_REMOVABLE:
&nbsp;&nbsp;//break;
