&nbsp;&nbsp;
&nbsp;&nbsp;if ( dwSize!=0 &amp;&amp; dwSize&lt;=sizeof(Buf) ) {
&nbsp;&nbsp;&nbsp;//pspdidd = (PSP_DEVICE_INTERFACE_DETAIL_DATA)HeapAlloc(GetProcessHeap(), HEAP_ZERO_MEMORY, dwSize);
